{
	"allOrNone": true,
	"compositeRequest": [{
			"method": "POST",
			"url": "/services/data/v{{version}}/sobjects/Account",
			"referenceId": "refAccount",
			"body": {
				"FirstName": "{{order_account_firstname}}",
				"LastName": "{{order_account_lastname}}",
				"PersonEmail": "{{order_account_email}}",
				"BillingStreet": "{{order_billing_address}}",
				"BillingCity": "{{order_billing_city}}",
				"BillingPostalCode": "{{order_billing_postal_code}}",
				"BillingState": "{{order_billing_state}}",
				"BillingCountry": "{{order_billing_country}}",
				"Phone": "{{order_billing_phone}}"
			}
		},
		{
			"method": "POST",
			"url": "/services/data/v{{version}}/sobjects/Order",
			"referenceId": "refOrder",
			"body": {
				"Pricebook2Id": "{{_pricebook_id}}",
				"Name": "{{order_account_firstname}} {{order_account_lastname}}",
				"Status": "Draft",
				"EffectiveDate": "{{_today}}",
				"OrderReferenceNumber": "{{_order_reference_number}}",
				"BillingStreet": "{{order_billing_address}}",
				"BillingCity": "{{order_billing_city}}",
				"BillingPostalCode": "{{order_billing_postal_code}}",
				"BillingState": "{{order_billing_state}}",
				"BillingCountry": "{{order_billing_country}}",
				"BillingPhoneNumber": "{{order_billing_phone}}",
				"OrderedDate": "{{_now}}",
				"BillingEmailAddress": "{{order_account_email}}",
				"AccountId": "@{refAccount.id}",
				"SalesChannelId": "{{_sales_channel_id}}"
			}
		},
		{
			"method": "POST",
			"url": "/services/data/v{{version}}/sobjects/PaymentGroup",
			"referenceId": "refPaymentGroup",
			"body": {
				"SourceObjectId": "@{refOrder.id}"
			}
		},
		{
			"method": "POST",
			"url": "/services/data/v{{version}}/composite/sobjects",
			"referenceId": "refGroup0",
			"body": {
				"allOrNone": true,
				"records": [{
						"attributes": {
							"type": "Product2"
						},
						"Description": "{{order_product1_name}}",
						"Name": "{{order_product1_name}}",
						"StockKeepingUnit": "{{order_product1_sku}}",
						"ProductCode": "{{order_product1_code}}",
