					},
					{
						"attributes": {
							"type": "Product2"
						},
						"Description": "{{order_product2_name}}",
						"Name": "{{order_product2_name}}",
						"StockKeepingUnit": "{{order_product2_sku}}",
						"ProductCode": "{{order_product2_code}}",
						"IsActive": true
					},
					{
						"attributes": {
							"type": "CardPaymentMethod"
						},
						"CardType": "Visa",
