				"records": [{
						"attributes": {
							"type": "PricebookEntry"
						},
						"Pricebook2Id": "{{_pricebook_id}}",
						"UnitPrice": {{order_product1_price}},
						"Product2Id": "@{refGroup0[0].id}"
					},
					{
						"attributes": {
							"type": "PricebookEntry"
						},
						"Pricebook2Id": "{{_pricebook_id}}",
						"UnitPrice": {{order_product2_price}},
						"Product2Id": "@{refGroup0[1].id}"
					},
