						"PricebookEntryId": "@{refGroup1[0].id}",
						"Product2Id": "@{refGroup0[0].id}"
					},
					{
						"attributes": {
							"type": "OrderItem"
						},
						"Description": "{{order_product2_name}}",
						"Type": "Order Product",
						"Quantity": 1,
						"TotalLineAmount": {{order_product2_price}},
						"LineNumber": 2,
						"UnitPrice": {{order_product2_price}},
						"OrderId": "@{refOrder.id}",
						"OrderDeliveryGroupId": "@{refGroup1[2].id}",
						"PricebookEntryId": "@{refGroup1[1].id}",
						"Product2Id": "@{refGroup0[1].id}"
					},
					{
						"attributes": {
							"type": "OrderItem"
						},
						"Description": "Shipping",
						"Product2Id": "{{_order_delivery_method_product_id}}",
						"Type": "Delivery Charge",
						"Quantity": 1,
						"TotalLineAmount": {{_order_delivery_method_product_pricebook_entry_unit_price}},
						"LineNumber": 1000,
						"UnitPrice": {{_order_delivery_method_product_pricebook_entry_unit_price}},
						"PricebookEntryId": "{{_order_delivery_method_product_pricebook_entry_id}}",
						"OrderId": "@{refOrder.id}",
						"OrderDeliveryGroupId": "@{refGroup1[2].id}"
					},
					{
						"attributes": {
							"type": "PaymentGatewayLog"
						},
						"InteractionStatus": "Success",
						"InteractionType": "Authorization",
						"ReferencedEntityId": "@{refGroup1[3].id}"
					}
				]
			}
		},
		{
			"method": "POST",
			"url": "/services/data/v{{version}}/composite/sobjects",
			"referenceId": "refGroup3",
			"body": {
				"allOrNone": true,
				"records": [{
						"attributes": {
							"type": "OrderItemTaxLineItem"
						},
						"Name": "{{order_product1_code}} - Tax",
						"Type": "Estimated",
						"Amount": {{_order_product1_tax_amount}},
						"Rate": {{order_tax_rate}},
						"TaxEffectiveDate": "{{_now}}",
						"OrderItemId": "@{refGroup2[0].id}"
					},
					{
						"attributes": {
							"type": "OrderItemTaxLineItem"
						},
						"Name": "{{order_product2_code}} - Tax",
						"Type": "Estimated",
						"Amount": {{_order_product2_tax_amount}},
						"Rate": {{order_tax_rate}},
						"TaxEffectiveDate": "{{_now}}",
						"OrderItemId": "@{refGroup2[1].id}"
					},
					{
						"attributes": {
							"type": "OrderItemTaxLineItem"
						},
						"Name": "DeliveryCharge - Tax",
						"Type": "Estimated",
						"Amount": {{_order_delivery_tax_amount}},
						"Rate": {{order_tax_rate}},
						"TaxEffectiveDate": "{{_now}}",
						"OrderItemId": "@{refGroup2[2].id}"
					}
				]
			}
		},
		{
			"method": "PATCH",
			"url": "/services/data/v{{version}}/sobjects/Order/@{refOrder.id}",
			"referenceId": "refUpdateOrder",
			"body": {
				"Status": "Activated"
